package _01_print;

public class T02_printf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			System.out.printf("%d\n",3);
			System.out.printf("좀비 슬라임 %d마리 죽여 %d의 경험치를 얻었습니다.\n",3,6);
			System.out.printf("현재는 %d년 %d월 이고, 우리나라의 평균키는 %.1f입니다.",2024,5,165.7);
			System.out.printf("이 과정의 정원은 %d이고, 현재 %d 명 수강중 입니다. %d%%를 달성했습니다.",25,25,100);
	}

}
