package _01_array;

public class T01_1array {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] arrayInt = new int[10];
		arrayInt[0] = 10;
		arrayInt[2] = 20;
		
		for(int i = 0;i<5;i++)
			System.out.println(arrayInt[i]);
		
		int[] arrayInt2 = {1,2,3,4,5};
		
		for(int i = 0;i<5;i++)
			System.out.println(arrayInt2[i]);
		
	}

}
